#!/bin/bash

tshark -Y 'tcp.analysis.retransmission  and not ip.dst_host matches "192.168.*.*"  and not ip.dst_host matches "10.*.*.*"  and not ip.dst_host matches "172.16.*.*"  and not ip.dst_host matches "172.17.*.*"  and not ip.dst_host matches "172.18.*.*"  and not ip.dst_host matches "172.19.*.*"  and not ip.dst_host matches "172.20.*.*"  and not ip.dst_host matches "172.21.*.*"  and not ip.dst_host matches "172.22.*.*"  and not ip.dst_host matches "172.23.*.*"  and not ip.dst_host matches "172.24.*.*"  and not ip.dst_host matches "172.25.*.*"  and not ip.dst_host matches "172.26.*.*"  and not ip.dst_host matches "172.27.*.*"  and not ip.dst_host matches "172.28.*.*"  and not ip.dst_host matches "172.29.*.*"  and not ip.dst_host matches "172.30.*.*"  and not ip.dst_host matches "172.31.*.*"'   -Tfields -e ip.dst -e tcp.dstport -i wlp2s0  -l   | awk -F '[\t ]' '{system("curl http://127.0.0.1:23456/?addr="$1"\\&port="$2)}' &